import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';

const fetchTags = async () => {
  
  const response = await fetch('/api/collections/tags');
  const data = await response.data.name.json();
  return data;
};

const TagInput = ({ tags, setTags }) => {
  const [suggestions, setSuggestions] = useState([]);
  const [value, setValue] = useState('');

  useEffect(() => {
    // Загрузка существующих тегов
    fetchTags().then((tags) => {
      setSuggestions(tags);
    });
  }, []);

  const onChange = (event, { newValue }) => {
    setValue(newValue);
  };

  const onSuggestionsFetchRequested = ({ value }) => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    setSuggestions(suggestions.filter(tag =>
      tag.toLowerCase().slice(0, inputLength) === inputValue
    ));
  };

  const onSuggestionSelected = (event, { suggestionValue }) => {
    setTags([...tags, suggestionValue]);
    setValue('');
  };

  const getSuggestionValue = (suggestion) => suggestion;

  const renderSuggestion = (suggestion) => (
    <div>{suggestion}</div>
  );

  const inputProps = {
    placeholder: 'Type a tag',
    value,
    onChange,
  };

  return (
    <div>
      <Autosuggest
        suggestions={suggestions}
        onSuggestionsFetchRequested={onSuggestionsFetchRequested}
        onSuggestionsClearRequested={() => setSuggestions([])}
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
        onSuggestionSelected={onSuggestionSelected}
      />
      <div>
        {tags.map((tag, index) => (
          <span key={index} className="badge bg-secondary me-2">
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
};

export default TagInput;
