import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import LogoutButton from './LogoutButton';
import logo_light from '../images/logo_light.jpg'


const Collections = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');
  const [collections, setCollections] = useState([]);

  useEffect(() => {

    const token = localStorage.getItem('token');
    if(token){
      const fetchUsername = async () => {
        try{
          const responce = await axios.get(`http://localhost:5000/api/users/me`, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}`}});
          setUserName(responce.data.name);
          //console.log(`${responce.data.name}`);
        }
        catch (error){
          console.error('error fetch user name: ', error);
        }
      };
      fetchUsername();
    }
    
    axios.get('http://localhost:5000/api/collections')
      .then(response => setCollections(response.data))
      .catch(error => console.error(error));
  }, [navigate]);

  const handleLogin = () => {
    navigate('/login');
  };
  const handleHomePage = () => {
    navigate('/collections');
  };

//<span className="me-3">{userName}</span>     <div className="d-flex justify-content-end align-items-start">   </div>

  return (
    
    <div className="container-fluid">

      <div className="container mt-3 d-flex justify-content-between align-items-center">

        <div className="d-flex justify-content-start align-items-start">
            <button className="btn btn-wight me-2" onClick={handleHomePage}>
              <img src={logo_light} alt="Unblock" style={{ width: '350px' }} />
            </button>
        </div>
        <div className="d-flex justify-content-end align-items-start"> 
          <div className="d-flex align-items-center">
            {userName ? (
              <>
                <button className="btn btn-primary me-3">{userName}</button>
                <LogoutButton />
              </>
            ) : (
              <button className="btn btn-primary me-2" onClick={handleLogin}>Log In</button>
            )}
            
          </div>
        </div>
      
      </div>

      <div className="container mt-5">
        <div className="d-flex justify-content-between align-items-center">
          <h2>Your Collections</h2>
          
        </div>

        
        <ul className="list-group">
          {collections.map(collection => (
            <li key={collection.id} className="list-group-item">
              {collection.title}
            </li>
          ))}
        </ul>
      </div>

    </div>
  );
};

export default Collections;
