import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import LogoutButton from './LogoutButton';


const Collections = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');
  const [collections, setCollections] = useState([]);

  useEffect(() => {

    const token = localStorage.getItem('token');
    if(token){
      const fetchUsername = async () => {
        try{
          const responce = await axios.get(`http://localhost:5000/api/users/me`, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}`}});
          setUserName(responce.data.name);
        }
        catch{
          console.log('error fetch user name');
        }
      };
    }
    
    axios.get('http://localhost:5000/api/collections')
      .then(response => setCollections(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleLogin = () => {
    navigate('/login');
  };

//<span className="me-3">{userName}</span>

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center">
        <h2>Your Collections</h2>
        <div className="d-flex align-items-center">
          {userName ? (
            <>
              <button className="me-3">{userName}</button>
              <LogoutButton />
            </>
          ) : (
            <button className="btn btn-danger me-2" onClick={handleLogin}>Log In</button>
          )}
          <LogoutButton />
        </div>
      </div>

      
      <ul className="list-group">
        {collections.map(collection => (
          <li key={collection.id} className="list-group-item">
            {collection.title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Collections;
