import './mainPage.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';

const TagCloud = ({ tags }) => {
  // Функция для определения размера шрифта на основе популярности тега
  const getFontSize = (count) => {
    if (count > 10) return '2rem';
    if (count > 5) return '1.5rem';
    return '1rem';
  };

  return (
    <div className="tag-cloud">
      {tags.map((tag, index) => (
        <span
          key={index}
          style={{ fontSize: getFontSize(tag.count), margin: '0.5rem', display: 'inline-block' }}
          className="tag"
        >
          {tag.name}
        </span>
      ))}
    </div>
  );
};

export default TagCloud;
