import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import 'bootstrap/dist/css/bootstrap.min.css';

const apiUrl = process.env.REACT_APP_API_URL;

const fetchTags = async () => {
  try {
    const response = await fetch(`${apiUrl}/api/collections/tags`);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching tags:', error);
    return [];
  }
};

const TagsInput = ({ tags, setTags }) => {
  const [allSuggestions, setAllSuggestions] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [value, setValue] = useState('');
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    fetchTags().then((tags) => {
      setAllSuggestions(tags);
      setSuggestions(tags);
      console.log('onSuggestionsFetchRequested 1 tags:', tags, allSuggestions, suggestions);
    });
  }, []);

  const onChange = (event, { newValue }) => {
    setValue(newValue);
    //console.log('onSuggestionsFetchRequested 1 tags:', tags, allSuggestions, suggestions);
  };

  const onSuggestionsFetchRequested = ({ value }) => {
    console.log('onSuggestionsFetchRequested 1 tags:', tags, allSuggestions, suggestions);
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    const filteredSuggestions = allSuggestions.filter(tag =>
      tag.name.toLowerCase().slice(0, inputLength) === inputValue
    );

    setSuggestions(filteredSuggestions);
    console.log('onSuggestionsFetchRequested 1 tags:', tags, allSuggestions, suggestions);
  };

  const onSuggestionSelected = (event, { suggestion }) => {
    console.log('onSuggestionsFetchRequested 1 tags:', tags, allSuggestions, suggestions);
    if (!tags.includes(suggestion.name)) {
      setTags([...tags, suggestion.name]);
      console.log('onSuggestionsFetchRequested 1 tags:', tags, allSuggestions, suggestions);
    }
    setValue('');
  };

  const addCustomTag = () => {
    const trimmedValue = value.trim();
    if (trimmedValue && !tags.includes(trimmedValue)) {
      setTags([...tags, trimmedValue]);
      setValue('');
    }
  };

  const getSuggestionValue = (suggestion) => suggestion.name;

  const renderSuggestion = (suggestion) => (
    <div>{suggestion.name}</div>
  );

  const inputProps = {
    placeholder: 'Type a tag',
    value,
    onChange,
    onKeyDown: (event) => {
      if (event.key === 'Enter') {
        event.preventDefault();
        addCustomTag();
      }
    },
    className: 'form-control me-2'
  };

  return (
    <div className="me-2">
      <div  className="d-flex">
        <Autosuggest
        suggestions={suggestions}
        onSuggestionsFetchRequested={onSuggestionsFetchRequested}
        onSuggestionsClearRequested={() => setSuggestions(allSuggestions)}
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
        onSuggestionSelected={onSuggestionSelected}
      />
      <button className="btn btn-primary" onClick={addCustomTag}>
        Add Custom Tag
      </button>
      </div>
      
      <div className="mt-2">
        {tags.map((tag, index) => (
          <span key={index} className="badge bg-secondary me-2 mb-1">
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
};

export default TagsInput;
