import React, { useState, useEffect } from 'react';
import '../index.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { getPopularTags } from './utility';

const CloudTags = async () => {
  const [tags, setTags] = useState([]);
  const quantity = 20;
  try {
    await getPopularTags(setTags, quantity);
  }
  catch (err) {
    console.err('error fetch tags: ',err);
  }

  console.log('cloud tags: ',tags);

  const getFontSize = (count) => {
    if (count > 4) return '2rem';
    console.log('cloud tags: ', count);
    if (count > 2) return '1.5rem';
    return '1rem';
  };

  return (
    <div className="tag-cloud">
      {tags
        .filter(tag => tag.count <= 1) 
        .map((tag) => (
        <span
          key={tag.id}
          style={{ fontSize: getFontSize(tag.count), margin: '0.5rem', display: 'inline-block' }}
          className="tag"
        >
          {tag.name}
        </span>
      ))}
    </div> 
  );
};

export default CloudTags;
