import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';

const apiUrl = process.env.REACT_APP_API_URL;

const fetchTags = async () => {
  try {
    const response = await fetch(`${apiUrl}/api/collections/tags`);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching tags:', error);
    return [];
  }
};

const TagsInput = ({ tags, setTags }) => {
  const [allSuggestions, setAllSuggestions] = useState([]); // Сохраняем исходные теги
  const [suggestions, setSuggestions] = useState([]);
  const [value, setValue] = useState('');

  useEffect(() => {
    fetchTags().then((tags) => {
      setAllSuggestions(tags); // Сохраняем все теги в allSuggestions
      setSuggestions(tags); // Инициализируем suggestions всеми тегами
    });
  }, []);

  const onChange = (event, { newValue }) => {
    setValue(newValue);
  };

  const onSuggestionsFetchRequested = ({ value }) => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    // Фильтруем из исходного массива, чтобы не терять данные
    const filteredSuggestions = allSuggestions.filter(tag =>
      tag.name.toLowerCase().slice(0, inputLength) === inputValue
    );

    setSuggestions(filteredSuggestions);
  };

  const onSuggestionSelected = (event, { suggestion }) => {
    setTags([...tags, suggestion.name]); // Добавляем новый тег в массив tags
    setValue(''); // Очищаем поле ввода
  };

  const getSuggestionValue = (suggestion) => suggestion.name;

  const renderSuggestion = (suggestion) => (
    <div>{suggestion.name}</div>
  );

  const inputProps = {
    placeholder: 'Tags',
    value,
    onChange,
  };

  return (
    <div>
      <Autosuggest
        suggestions={suggestions}
        onSuggestionsFetchRequested={onSuggestionsFetchRequested}
        onSuggestionsClearRequested={() => setSuggestions(allSuggestions)} // Возвращаем все исходные теги
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
        onSuggestionSelected={onSuggestionSelected}
      />
      <div>
        {tags.map((tag, index) => (
          <span key={index} className="badge bg-secondary me-2">
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
};

export default TagsInput;
