import React, { useState, useEffect } from 'react';
import '../index.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import LogoutButton from './LogoutButton';

import { getUserName, getCollections, getCategories } from './utility';

import logo_light from '../images/logo_light.jpg'
import 'bootstrap-icons/font/bootstrap-icons.css';
import { useParams } from 'react-router-dom'; //доступ к параметрам url

const apiUrl = process.env.REACT_APP_API_URL;

const CreateCollection = () => {
  const { id } = useParams(); //тут берется id нужной коллекции
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');
  const [userId, setUserId] = useState(null);
  
  const [collection, setCollection] = useState(null);

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [error, setError] = useState('');

  const [categories, setCategories] = useState([]);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getUserName(setUserName, setUserId);
    if (id) {

      getCollections(setCollection, '/api/collectionEditView/viewCollection', id);
      setTitle(collection.title);
      setDescription(collection.description);
      setImageUrl(collection.image_url);
      setError('error loading collection data');
      setLoading(false);
    }
    else {
      setLoading(false);
    }
    
  }, [navigate, id]); /*, collection*/

  const handleLogin = () => {
    navigate('/login');
  };
  const handleHomePage = () => {
    navigate('/collections');
  };
  const handleProfile = () => {
    navigate(`/profile/${userId}`);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const data = { title, description, imageUrl };
      
      if (id) {
        // Режим редактирования
        await axios.put(`${apiUrl}/api/collections/${id}`, data, {
          headers: { Authorization: `Bearer ${token}` }
        });
      } else {
        // Режим создания
        await axios.post(`${apiUrl}/api/collections`, data, {
          headers: { Authorization: `Bearer ${token}` }
        });
      }

      navigate('/collections'); // Перенаправление на страницу коллекций после успешного сохранения
    } catch (error) {
      console.error('Error saving collection:', error);
      setError('Failed to save collection');
    }
  };

  const handleImageUpload = (e) => {
    //navigate('/login');
  };

  if (loading) {
    return <div>Loading...</div>; 
  }

  return (

    <div className="container-fluid">

      <div className="container mt-3 d-flex justify-content-between align-items-center">

        <div className="d-flex justify-content-start align-items-start">
            <button className="btn btn-wight me-2" onClick={handleHomePage}>
              <img src={logo_light} alt="Unblock" style={{ width: '350px' }} />
            </button>
        </div>
        <div className="d-flex justify-content-end align-items-start"> 
          <div className="d-flex align-items-center">
            {userName ? (
              <>
                <button className="btn btn-primary me-3" onClick={handleProfile}>{userName}</button>
                <LogoutButton />
              </>
            ) : (
              <button className="btn btn-primary me-2" onClick={handleLogin}>Log In</button>
            )}
            
          </div>
        </div>
      
      </div>


      <div className="container">

        <h2>{id ? 'Edit Collection' : 'Create New Collection'}</h2>

        <form onSubmit={handleSubmit}>
          {/* Форма для загрузки картинки */}
          <div className="mb-3">
            <label htmlFor="imageUpload" className="form-label">Upload Image</label>
            <input
              type="file"
              id="imageUpload"
              className="form-control"
              onChange={(e) => handleImageUpload(e)} // функция для обработки загрузки файла
            />
          </div>

          {/* Поле для ввода названия и выпадающий список категорий */}
          <div className="d-flex mb-3">
            <div className="me-2" style={{ flex: 1 }}>
              <label htmlFor="title" className="form-label">Title</label>
              <input
                type="text"
                id="title"
                className="form-control"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </div>
            <div className="ms-2" style={{ flex: 1 }}>
              <label htmlFor="category" className="form-label">Category</label>
              <select
                id="category"
                className="form-select"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
              >
                <option value="">Select a category</option>
                {categories?.map((cat) => (
                  <option key={cat.id} value={cat.id}>{cat.name}</option>
                ))}
              </select>
            </div>
          </div>

          {/* Поле для описания */}
          <div className="mb-3">
            <label htmlFor="description" className="form-label">Description</label>
            <textarea
              id="description"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </div>

          {/* Форма добавления нового элемента в коллекцию */}
          <div className="mb-3">
            <h5>Add New Item</h5>
            <div className="d-flex">
              <input
                type="text"
                className="form-control me-2"
                placeholder="Item Name"
                value={newItemName}
                onChange={(e) => setNewItemName(e.target.value)}
              />
              <input
                type="text"
                className="form-control me-2"
                placeholder="Tags (comma separated)"
                value={newItemTags}
                onChange={(e) => setNewItemTags(e.target.value)}
              />
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleAddItem}
              >
                Add Item
              </button>
            </div>
          </div>

          {/* Отображение элементов коллекции */}
          <div className="elementsContainer d-flex flex-wrap flex-column" style={{ width: '300px' }}>
            {collection.items?.map((item) => (
              <div key={item.id} className="card mt-0 mb-2" style={{ width: '100%' }}>
                <div className="card-body">
                  <h5 className="card-title mt-0">{item.name}</h5>
                  <div className="tags mt-2">
                    {item.tags?.map((tag) => (
                      <span key={tag.id} className="badge bg-secondary me-2 mb-1">
                        <Link to={`/profile/${userId}`} className="linkToProfileVC text-white" style={{ textDecoration: 'none' }}>
                          {tag.name}
                        </Link>
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {error && <div className="alert alert-danger">{error}</div>}
          <button type="submit" className="btn btn-primary">
            {id ? 'Save Changes' : 'Create'}
          </button>
        </form>

      </div>


    </div>
  );
};

export default CreateCollection;
